function indexWindowsOnload(){for(var n=document.getElementsByClassName("banner-swapping"),e=0;e<n.length;){var s=n[e];fetch("/assets/images/banners/manifest.json").then(n=>n.json()).then(n=>{var e=Math.floor(Math.random()*(n.length+1));setInterval(function(){s.style.backgroundImage=`url(/assets/images/banners/${n[e%n.length]})`,e++},5e3)}),e++}for(var a=document.getElementsByClassName("no-js-hide");0<a.length;)a[0].classList.remove("no-js-hide")}window.onload=indexWindowsOnload;